import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {

const [post, setPosts ] = useState ([]);

useEffect(() => {
  axios.get('https://jsonplaceholder.typicode.com/posts/1')
    .then(response => {
      setPosts(response.data);
    })
    .catch(error => {
      console.error(error);
    });
}, []);

if (!post) return null;



return (

  <div class="container">
    <div class="row">
      <h1> Graph Area </h1>
    </div>
    <div class="row">
        <div class="col-lg-4">
                    
          <h2> Rentals </h2>
          <span> Rent due this month: </span>
          <button class="btn btn-large"> Pay Rent Now </button>
          <button class="btn btn-large"> View Rental Statements </button>

        </div> 
        <div class="col-lg-4">
                  
          <h1> {post.title} </h1>
          <p>{post.body}</p>
              
          
        </div>   

        <div class="col-lg-4">
                      
          <h1> {post.title} </h1>
            <p>{post.body}</p>
              
          
        </div>
      </div>


  </div>


 
       
      
    )
  }

export default Home;
