function PayRent(){
    const [post, setPosts ] = useState ([]);

    const url = "https://sandbox.payfast.co.za/eng/process?merchant_id=10018036&merchant_key=v113p2nf34wqz&return_url=https%3A%2F%2F6d6a14b0.ngrok.io%2Fhome%2Freturn&email_address=sbtu01%40payfast.co.za&m_payment_id=8d00bf49-e979-4004-228c-08d452b86380&amount=30&item_name=Once+off+option&item_description=Some+details+about+the+once+off+payment&email_confirmation=1&confirmation_address=sbtu01%40payfast.co.za&signature=eb63a826f61c5adb1b35b0b0b3c2b4a6";
    const url2 = "https://facebook.com";
   

        useEffect(() => {
            axios.post(url)
              .then(response => {
                setPosts(response.data);
                console.log(response.data);
              })
              .catch(error => {
                console.error(error);
              });
          }, []);     
       
    
    return(
        <div className="container">
            <div className="row">
                <h4> Pay Rent Now </h4>
            </div>
            <div className="row payRentDetails">
                <div class="col-6 personalRentDetails">
                    <p> Dear: </p>
                    <p> Rental due this month is : </p>
                    <button type="button" class="btn btn-primary paymentBtn"> Pay Now </button>
                </div>
                <div class="col-6 paymentOptions">
                <p> Supported payment methods </p>
                    <div className="paymentOptions">
                        <img class="paymentImages" src={logo} alt="Logo" height={100} width={100} />
                        <img class="paymentImages" src={logo} alt="Logo" height={100} width={100} />
                    </div>
                </div>                
            </div>
            <div className="row">
                        <p> You will be redirected to a  payment window where you can chose</p>
            </div>

            <div className="row">
                <div className="previousRentals">
                    <h4> Previous Rental Statements </h4>
                    <table class="table table-striped">
                        <thead class="thead-dark">
                            <tr>
                                <th> Date </th>
                                <th> Status </th>
                                <th> Action </th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>2 March</td>
                                <td> Paid </td>
                                <td><button class="btn btn-primary"> Download </button></td>
                            </tr>
                        </tbody>
                    </table>

                    <div className="paymentOptions">
                        <img></img>
                    </div>

                    
                </div>
                             

            </div>
        </div>
    )
}

export default PayRent;
