import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../home.css';

function Home() {

const [post, setPosts ] = useState ([]);

useEffect(() => {
  axios.get('https://jsonplaceholder.typicode.com/posts/1')
    .then(response => {
      setPosts(response.data);
    })
    .catch(error => {
      console.error(error);
    });
}, []);

if (!post) return null;



return (

  <div class="container">
    <div class="row">
      <h1> Graph Area </h1>
    </div>
    <div class="row">
        <div class="col-lg-4">
          <div class="home-tiles">
            <h2> Rentals </h2>
            <p> Rent due this month: </p>
            <button class="btn btn-large btn-primary"> Pay Rent Now </button>
            <p> Click the button below to view Rental Statements</p>
            <button class="btn btn-large btn-primary"> View Statements </button>
          </div> 
        </div> 
        <div class="col-lg-4">                  
          <div class="home-tiles">
              <h2> Visitors </h2>
              <p> Generate Visitors Code </p>
              <button class="btn btn-large btn-primary"> Generate Code </button>
              <p> Schedule a Delivery </p>
              <button class="btn btn-large btn-primary"> Delivery Code </button>
            </div>  
        </div>   

        <div class="col-lg-4">
          <div class="home-tiles">
              <h2> Meter Usage </h2>
              <p> View Meter Usage </p>
              <button class="btn btn-large btn-primary"> View Meter </button>
              <p> Schedule a Delivery </p>
              <button class="btn btn-large btn-primary"> Delivery Code </button>
          </div>            
          
        </div>
      </div>


  </div>


 
       
      
    )
  }

export default Home;
