import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { ModalHeader } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';

import '../pages/payRent.css';

import logo from '../Images/clubhouse.jpg';
import eft from '../Images/Instant-Eft.png';
import visa from '../Images/visa-mastercard.png';


/*function PayNow (){
    
   //e.preventDefault();
    const url = "https://sandbox.payfast.co.za/eng/process?merchant_id=10018036&merchant_key=v113p2nf34wqz&return_url=https%3A%2F%2F6d6a14b0.ngrok.io%2Fhome%2Freturn&email_address=sbtu01%40payfast.co.za&m_payment_id=8d00bf49-e979-4004-228c-08d452b86380&amount=30&item_name=Once+off+option&item_description=Some+details+about+the+once+off+payment&email_confirmation=1&confirmation_address=sbtu01%40payfast.co.za&signature=eb63a826f61c5adb1b35b0b0b3c2b4a6";
    const url2 = "https://facebook.com";
    axios( url2, {
        method: 'POST',
        origin: 'https://localhost:3000',
        exposeHeaders: "access-control-allow-origin,access-control-allow-methods,access-control-allow-headers",
        headers: {
            //'Access-Control-Allow-Origin': 'https://localhost:3000',
            //'Content-Type': 'application/json',
            //'Access-Control-Allow-Methods': 'GET, POST , OPTIONS, DELETE, PUT',
            //'Access-Control-Allow-Headers': 'Origin, X-Requested-With, Content-Type, Accept',

            'Access-Control-Allow-Origin':'https://www.facebook.com',
            'Access-Control-Allow-Credentials': true,
            'Access-Control-Allow-Methods':'POST',
            'Access-Control-Allow-Headers': 'X-PINGOTHER, Content-Type',
            'Access-Control-Max-Age':86400,
            'Content-Type':'application/json',
            //'Content-Length': 0,
        },
        credentials: 'include',
        //crossOrigin: true,
    })

   

}*/

function PayRent(){
    //const [post, setPosts ] = useState ([]);

    //const url = "https://sandbox.payfast.co.za/eng/process?merchant_id=10018036&merchant_key=v113p2nf34wqz&return_url=https%3A%2F%2F6d6a14b0.ngrok.io%2Fhome%2Freturn&email_address=sbtu01%40payfast.co.za&m_payment_id=8d00bf49-e979-4004-228c-08d452b86380&amount=30&item_name=Once+off+option&item_description=Some+details+about+the+once+off+payment&email_confirmation=1&confirmation_address=sbtu01%40payfast.co.za&signature=eb63a826f61c5adb1b35b0b0b3c2b4a6";
    //const url2 = "https://facebook.com";
   

     
          
          //Previous Rentals for all tenants

    const [ currentRent, setCurrentRent ] = useState([]);

    var userProfileId = "mbonisi@mdotcreatives.co.za"

    const urlCurrentRent = "https://localhost:44311/api/controller/GetUserRent?ProfileId=mbonisi@mdotcreatives";

    useEffect(() => {
        axios.get(urlCurrentRent)
            .then(response => {
                
                const oneRent = response.data;
                console.log("Rent for Mbonisi :" + JSON.stringify(oneRent[0]));
                setCurrentRent(oneRent[0]);
                
                
            })
            .catch(error=>{
                console.log(error);
            });

    }, []);

    const [ allRentals, setAllRentals ] = useState([]);
    
    const urlRentals = "https://localhost:44311/api/controller/GetAllRents";
    useEffect( () => {

        /*include code to check if this admin or a normal user 
        * if user pass the user name to the url
        */
        axios.get(urlRentals)
        .then(response =>{
            setAllRentals(response.data);
            console.log(response.data);
        })
        .catch(error =>{
            console.error(error);
        });
    },[]);

    //Modal to handle showing previous rental payments for a single tenant
    const [ show, setShow ] = useState(false);

    const handleClose = () => setShow(false); 

    const HandleShow = () => setShow(true);
       
    
    return(
        <div className="container">
            <div className="row">
                <h4> Pay Rent Now </h4>
            </div>
            <div className="row payRentDetails">
                <div class="col-4 personalRentDetails">
                    <p> Dear: </p>                    
                       <p> Rental due this month is : <span> { currentRent.rentPrice} </span> </p>
                        
                    
                  
                </div>
                <div class="col-8 paymentOptions">
                    <button type="button" class="btn btn-primary btn-lg paymentBtn"> Pay Now </button>
                    <p> Supported payment methods </p>
                    <div className="paymentOptions">
                        <img class="paymentImages" src={eft} alt="Logo" height={100} width={200} />
                        <img class="paymentImages" src={visa} alt="Logo" height={100} width={200} />
                    </div>
                </div>  
                <div className="col-12">
                            <p> You will be redirected to a  payment window where you can chose</p>
                </div>


            </div>


            <div className="row">
                <div className="previousRentals">
                    <h4> Previous Rental Statements </h4>
                    <table class="table table-striped">
                        <thead class="thead-dark">
                            <tr>
                                <th> Rent Id </th>
                                <th> Profile Id</th>
                                <th> Unit No </th>
                                <th> Due Date </th>
                                <th> Payment Date </th>
                                <th> Rent Price (R) </th>
                                <th> Action </th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                            allRentals.map((rent, index) => {
                                return <tr key={index}>
                                    <td>{rent.rentId}</td>
                                    <td>{rent.profileId}</td>
                                    <td>{rent.unitNo}</td>
                                    <td>{rent.dueDate}</td>
                                    <td>{rent.paymentDate}</td>
                                    <td>{rent.rentPrice}</td>
                                    <td>
                                        <Button 
                                            variant="btn btn-primary"
                                            onClick={HandleShow}>
                                            View Previous Rental Payments
                                        </Button>
                                        <Modal
                                            show={show}
                                            handle={handleClose}
                                            backdrop="static"
                                            size="lg"
                                            aria-labelledby="contained-modal-title-vcenter"
                                            centered
                                            keyboard={false}
                                        >
                                        <ModalHeader>
                                            <Modal.Title> 
                                                Smart Living Rental Property Terms and Conditions 
                                            </Modal.Title>
                                        </ModalHeader>
                                        <Modal.Body>
                                        <table class="table table-striped">
                                            <thead class="thead-dark">
                                                <tr>
                                                    <th> Rent Id </th>
                                                    <th> Profile Id</th>
                                                    <th> Unit No </th>
                                                    <th> Due Date </th>
                                                    <th> Payment Date </th>
                                                    <th> Rent Price </th>
                                                    <th> Action </th>
                                                </tr>
                                            </thead>
                                            <tbody>
                               
                                                <tr>
                                                    <td></td>
                                                    <td></td>
                                                    <td></td>
                                                    <td></td>
                                                    <td></td>
                                                    <td></td>
                                                    <td>
                                                        <button className="btn btn-primary">
                                                            Simple Button
                                                        </button>
                                                    </td>
                                                </tr>
                                                
                                            </tbody>
                                        </table>
                                        </Modal.Body>
                                            <Modal.Footer>
                                                <Button variant="secondary"onClick={handleClose}> Close </Button>
                                            </Modal.Footer>
                                        </Modal>
                                    </td>
                                </tr>
                            })
                        }
                        </tbody>
                    </table>

                    <div className="paymentOptions">
                        <img></img>
                    </div>

                    
                </div>
                             

            </div>
        </div>
    )
}

export default PayRent;
